<?php   use yii\helpers\Url;   ?><div class="home-slider owl-carousel owl-theme">	<?php	if($slides) {		foreach ($slides as $slide) {			if ($slide->image_path == "")				continue;			?>			<div class="item">				<img  alt="<?= $slide->name ?>" title="<?= $slide->name ?>" src="<?= Yii::$app->params['baseurl'] . "/uploads/slides/main/" . $slide->image_path ?>">				<div class="content">					<h2><?= $slide->name ?></h2>					<p><?= $slide->content ?></p>				</div>			</div>		<?php }	} ?></div>